import { useState, useEffect } from 'react';
import BlogList from './BlogList';

export default function Home() {

    const [blogs, setBlogs] = useState(null);

    const [isPending, setIsPending] = useState(true);
    // [ 
    //     {title: 'My New Website', body: 'lorem ipsum', author: 'ry', id: 1},
    //     {title: 'what\'s up', body: 'hi', author: 'ry', id: 2},
    // ]);

    const handleDelete = (id) => {
        const newBlog = blogs.filter((blog) => blog.id !== id);
        setBlogs(newBlog);
    }  

// USEEFFECT LESSON
// useful to fetch data

    //empty useEffect() runs at every render
    useEffect(() => {
        console.log('use effect ran');
        console.log(blogs)
    }); 

    //DONT set state here!

    //useeffect with dependencies
    //this one is basically componentdidmount or when the component renders for the first time
    // useEffect(() => {
    //     console.log('use effect ran again');
    //     console.log(blogs)
    // }, []); 
    useEffect(() => {
        //can't use async inside this but can externalize the async method if you want
        // fetch('http://localhost:8000/blogs')
        //     .then(response => {
        //         return response.json()
        //     })
        //     .then(data => {
        //         //we finally have the data smh
        //         console.log(data);
        //         setBlogs(data); //will this cause an infinite loop?
        //         setIsPending(false);
        //     })
        setTimeout(() => {
            fetch('http://localhost:8000/blogs')
                .then(response => {
                    return response.json()
                })
                .then(data => {
                    //we finally have the data smh
                    console.log(data);
                    setBlogs(data); //will this cause an infinite loop?
                    setIsPending(false);
                })
                .catch(err => console.log(err.message));
        }, 1000)
    }, []); 

    const [name, setName] = useState('mario')
    //state variable
    useEffect(() => {
        console.log('use effect ran 3');
        console.log(name);
    },[name])
    // const handleClick
    return(
        <div className="home">
            {/* <h2>Homepage</h2>
            {blogs.map((blog) => (
                <div className="blog-preview">
                    <h2>{blog.title}</h2>
                    <p>Written by {blog.author}</p>
                </div>
            ))} */}
            {/* <BlogList />  we need props! */}
            {/* <BlogList blogs={blogs.filter( (blogs) => blogs.id !== 2 )} title="!!" /> */}
            {isPending && <div>loading...</div>}
            {/* <BlogList blogs={blogs} title="" handleDelete ={handleDelete}/>  why does this error? */}
            {blogs && <BlogList blogs={blogs} title="" handleDelete ={handleDelete}/>}
            <button onClick={() => setName(name==='luigi'?'mario':'luigi')}>change name</button>
        </div>
    );
}
